<?phpnamespace App\AdminModule\Components\Article\TitleArticle;use Nette\Application\UI\Form;use Nette\Security\User;use DbTable;/** * Formular a jeho spracovanie pre zmenu urovne registracie polozky. * Posledna zmena 01.03.2016 *  * @author     Ing. Peter VOJTECH ml. <petak23@gmail.com> * @copyright  Copyright (c) 2012 - 2016 Ing. Peter VOJTECH ml. * @license * @link       http://petak23.echo-msz.eu * @version    1.0.1 */class ZmenUrovenRegistracieFormFactory {  /** @var DbTable\Hlavne_menu */	private $hlavne_menu;    /** @var \Nette\Database\Table\Selection */	private $registracia;    /**   * @param DbTable\Hlavne_menu $hlavne_menu   * @param DbTable\Registracia $registracia */  public function __construct(DbTable\Hlavne_menu $hlavne_menu, DbTable\Registracia $registracia, User $user) {		$this->hlavne_menu = $hlavne_menu;    $this->registracia = $registracia->urovneReg($user->getIdentity()->id_registracia);	}    /**   * Formular pre zmenu urovne registracie polozky.   * @param int $id Id polozky v hlavnom menu   * @param int $id_registracia Sucasna uroven registracie polozky   * @return Nette\Application\UI\Form */    public function create($id, $id_registracia)  {		$form = new Form();		$form->addProtection();    $form->addHidden("id", $id);    $form->addRadioList('id_registracia', 'NovÃ¡ ÃºroveÅˆ:', $this->registracia)         ->setDefaultValue($id_registracia);    $form->addSubmit('uloz', 'ZmeÅˆ')         ->setAttribute('class', 'btn btn-success')         ->onClick[] = [$this, 'zmenUrovenRegistracieFormSubmitted'];    $form->addSubmit('cancel', 'Cancel')         ->setAttribute('class', 'btn btn-default')         ->setAttribute('data-dismiss', 'modal')         ->setAttribute('aria-label', 'Close')         ->setValidationScope(FALSE);		return $form;	}    /**    * Spracovanie formulara pre zmenu vlastnika clanku.   * @param Nette\Forms\Controls\SubmitButton $button Data formulara */  public function zmenUrovenRegistracieFormSubmitted($button) {		$values = $button->getForm()->getValues(); 	//Nacitanie hodnot formulara    try {			$this->hlavne_menu->zmenUrovenRegistracie($values);		} catch (Database\DriverException $e) {			$button->addError($e->getMessage());		}  }}