<?phpnamespace App\AdminModule\Presenters;use DbTable;/** * DomovskÃ½ preseter administracie. * * Posledna zmena(last change): 06.06.2016 * *	Modul: ADMIN * * @author Ing. Peter VOJTECH ml. <petak23@gmail.com> * @copyright  Copyright (c) 2012 - 2016 Ing. Peter VOJTECH ml. * @license * @link       http://petak23.echo-msz.eu * @version 1.0.4 */class HomepagePresenter extends \App\AdminModule\Presenters\BasePresenter {    // -- DB  /** @var DbTable\Hlavne_menu_cast @inject */  public $hlavne_menu_cast;    /** Vychodzi render    * @param int $id Id casti hlavneho menu ako zaporne cislo */  public function renderDefault($id) {    $user = $this->user;    $this->template->id_menu = -1*$id;    $this->template->view_add_link = ($user->isLoggedIn() ? $user->isAllowed('Admin:Menu', 'edit') : FALSE) &&                                      ($user->isInRole("admin") ? TRUE :	$this->nastavenie['add_uroven0']) &&                                     ($id ? $this->id_reg >= $this->hlavne_menu_cast->find(-1*$id)->id_registracia : FALSE);	}}