<?phpnamespace App\AdminModule\Presenters\Forms\Verzie;use Nette\Application\UI\Form;class EditVerzieFormFactory {  /**   * Edit hlavne menu form component factory.   * @return Nette\Application\UI\Form   */    public function create($send_e_mail_news = FALSE)  {    $form = new Form();		$form->addProtection();    $form->addGroup();    $form->addHidden("id");    $form->addHidden("id_user_profiles");		$form->addText('cislo', 'Číslo verzie:', 50, 80)         ->setAttribute('autofocus', 'autofocus')				 ->addRule(Form::FILLED, 'Číslo verzie musí byť zadané!');		$form->addText('subory', 'Zmenené súbory:', 50, 80);    if ($send_e_mail_news) {      $form->addCheckbox('posli_news', ' Posielatie NEWS o tejto aktualite');    } else {      $form->addHidden("posli_news", FALSE);    }		$form->addTextArea('text', 'Popis zmien:', 60, 15);		$form->addSubmit('uloz', 'Ulož')         ->setAttribute('class', 'btn btn-success');    $form->addSubmit('cancel', 'Cancel')->setAttribute('class', 'btn btn-default')         ->setValidationScope(FALSE);		return $form;	}}