<?phpnamespace App\FrontModule\Components\Adresar;use Nette;/** * Komponenta pre zobrazenie jednej adresy z adresara pre FRONT modul * Posledna zmena(last change): 28.10.2015 * * @author Ing. Peter VOJTECH ml <petak23@gmail.com> * @copyright Copyright (c) 2012 - 2015 Ing. Peter VOJTECH ml. * @license * @link http://petak23.echo-msz.eu * @version 1.0.0 * */class ZobrazAdresuControl extends Nette\Application\UI\Control {  /** @var mix $aktualny_projekt Data zobrazovaneho clanku */  private $adresa;  public function __construct($adresa) {    parent::__construct();    $this->adresa = isset($adresa) ? $adresa : FALSE;  }  /** Render funkcia pre vypisanie odkazu na clanok    * @see Nette\Application\Control#render()   */  public function render() {     $this->template->setFile(__DIR__ . '/ZobrazAdresu.latte');    $this->template->adresa = $this->adresa;    $this->template->render();  }}