<?phpnamespace App\FrontModule\Components\Clanky;use Nette;use DbTable;/** * Komponenta pre zobrazenie aktualnych projektov pre FRONT modul * Posledna zmena(last change): 04.02.2016 * * @author Ing. Peter VOJTECH ml <petak23@gmail.com> * @copyright Copyright (c) 2012 - 2016 Ing. Peter VOJTECH ml. * @license * @link http://petak23.echo-msz.eu * @version 1.0.1 * */class AktualnyProjektControl extends Nette\Application\UI\Control {  /** @var array - texty pre sablonu v inom jazyku */  private $texty = array(    "h2"               =>"Aktuálny projekt:",    "text_title_image" => "Titulný obrázok",   );  /** @var DbTable\Hlavne_menu_lang */  private $hlavne_menu_lang;  /** @var string $avatar_path  Cesta k titulnemu obrazku clanku */  private $avatar_path = "";  /** @var int $language_id Id jazyka  */  private $language_id;  public function __construct(DbTable\Hlavne_menu_lang $hlavne_menu_lang) {    parent::__construct();    $this->hlavne_menu_lang = $hlavne_menu_lang;  }    /** Nastavenie textov pre sablonu v inom jazyku   *  @param array $text texty    */  public function setTexts($text) {    $this->texty = array_merge ($this->texty, $text);    return $this;  }    /** Nastavenie jazyka    *  @param int $language_id texty    */  public function setLanguage_id($language_id) {    $this->language_id = $language_id;    return $this;  }  /** Nastavenie hlavicky clanku   *  @param string $avatar_path Cesta k titulnemu obrazku clanku   */  public function setAvatarPath($avatar_path) {    $this->avatar_path = $avatar_path;    return $this;  }    /** Render funkcia pre vypisanie odkazu na clanok    * @see Nette\Application\Control#render()   */  public function render() {     $this->template->setFile(__DIR__ . '/AktualnyProjekt.latte');    $this->template->clanok = $this->hlavne_menu_lang->findBy(['hlavne_menu.aktualny_projekt'=>1, 'id_lang'=>$this->language_id]);    $this->template->texty = $this->texty;    $this->template->avatar_path = $this->avatar_path;    $this->template->render();  }}interface IAktualnyProjektControl {  /** @return AktualnyProjektControl */  function create();}