<?phpnamespace App\FrontModule\Components\Slider;use Nette\Application\UI\Control;use DbTable;/**  * Komponenta pre vykreslenie slider-u. *Posledna zmena(last change): 16.03.2016 * * @author Ing. Peter VOJTECH ml. <petak23@gmail.com> * @copyright  Copyright (c) 2013 - 2016 Ing. Peter VOJTECH ml. * @license * @link       http://petak23.echo-msz.eu * @version 1.0.5 */class SliderControl extends Control {  	/** @var DbTable\Slider */	private $slider;      	/** @var array */	private $nastavenie;  /**   * @param DbTable\Slider $slider */  public function __construct(DbTable\Slider $slider) {    parent::__construct();    $this->slider = $slider;	}    /**   * Nastavenie slider-u   * @param array $nastavenie   * @return \App\FrontModule\Components\Slider\SliderControl */  public function setNastavenie($nastavenie) {    $this->nastavenie = $nastavenie;    return $this;  }  	/**    * Pre vyhodnotenie zobrazenia		* @param mix     $z  zobrazenie polozky		* @param string  $p  nazov presentera		* @param int     $i  id pre zobrazenie		* @return boolean   */  private function _zisti($z, $p, $i) {    return $z == NULL ? TRUE : ($z == 0 && $p == 'Homepage' ? TRUE : ($z > 0 && $z == $i ? TRUE : FALSE));  	}  /** Render */	public function render() {    $this->template->setFile(__DIR__ . '/Slider_'.$this->nastavenie["varianta"].'.latte');    $p_name = explode(":", $this->presenter->name);    if ($this->nastavenie["varianta"] > 0) {      $slider = $this->slider->getSlider(/*$this->nastavenie["varianta"]==4 ? 'NEWID()' :'poradie ASC'*/);      $slider_zobrazenie = $slider->fetchPairs("id", "zobrazenie");      $id_pre_zobrazenie = ($p_name[1] == "Clanky") ? (isset($this->presenter->params['id']) ? $this->presenter->params['id'] : 0) : 0;      $vysa = [];      foreach ($slider_zobrazenie as $k => $v) {        $vy[$k] = strpos($v, " ") !== FALSE ? explode(" ", $v) : $v;        $vysledok = FALSE;        if (is_array($vy[$k])) {          foreach ($vy[$k] as $ke => $z) {            $vysledok = $this->_zisti($z, $p_name[1], $id_pre_zobrazenie) == TRUE ? TRUE : $vysledok;          }        } else {          $vysledok = $this->_zisti($vy[$k], $p_name[1], $id_pre_zobrazenie);        }        if ($vysledok == TRUE) { $vysa[] = $k;}      }      $this->template->id_pre_zobrazenie = $id_pre_zobrazenie;      $this->template->slider = $slider->where('id', $vysa)->fetchAll();      $this->template->nastavenie = $this->nastavenie;    } else {      $s = $this->slider->getSlider();      $this->template->slider = $s->limit(1,  (int)rand(0, count($s)-1))->fetch();    }    $this->template->p_name = $p_name;    $this->template->render();	}}interface ISliderControl {  /** @return SliderControl */  function create();}